      required: true,
      unique: false,
      trim: true,
    },

    marca: {
      type: String,
      required: true,
      unique: false,
      trim: true,
    },

    garantia_meses: {
      type: Number,
      required: true,
      unique: false,
    },

    color: {
      type: String,
      required: true,
      unique: false,
      trim: true,
    },
    material: {
      type: String,
      required: true,
      unique: false,
      trim: true,
